import sys

PY2 = sys.version_info[0] == 2

if not PY2:
    text_type = str
    string_types = (str,)
    unichr = chr
    int_types = (int,)

    def iteritems(x):
        return iter(x.items())

    from collections.abc import Mapping

else:
    text_type = __builtins__.unicode
    string_types = (str, __builtins__.unicode)
    unichr = __builtins__.unichr
    int_types = (int, __builtins__.long)

    def iteritems(x):
        return x.iteritems()

    from collections import Mapping
    Mapping
